CREATE TABLE "Course" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Description" text NOT NULL,
    "StudentId" integer NOT NULL,
    CONSTRAINT "PK_Course" PRIMARY KEY ("Id")
);

CREATE TABLE "Student" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "LastName" text NOT NULL,
    "Birthday" timestamp with time zone NOT NULL,
    "PhoneNumber" text NOT NULL,
    "Address" text NOT NULL,
    "Email" text NOT NULL,
    "CityId" integer NOT NULL,
    "CourseId" integer NOT NULL,
    CONSTRAINT "PK_Student" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Student_City_CityId" FOREIGN KEY ("CityId") REFERENCES "City" ("Id") ON DELETE CASCADE
);

CREATE TABLE "CourseStudent" (
    "CoursesId" integer NOT NULL,
    "StudentsId" integer NOT NULL,
    CONSTRAINT "PK_CourseStudent" PRIMARY KEY ("CoursesId", "StudentsId"),
    CONSTRAINT "FK_CourseStudent_Course_CoursesId" FOREIGN KEY ("CoursesId") REFERENCES "Course" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_CourseStudent_Student_StudentsId" FOREIGN KEY ("StudentsId") REFERENCES "Student" ("Id") ON DELETE CASCADE
);

CREATE INDEX "IX_CourseStudent_StudentsId" ON "CourseStudent" ("StudentsId");

